import React from "react";
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View , Text, Image } from "react-native";
import CardComponent from '../components/cardComponent';
import { FontAwesome } from '@expo/vector-icons';

export default function ReviewScreen({ route, navigation}){
    const gameItem  = route.params;
    const Rating = ()=>{
        for (let index = 0; index < gameItem.rating; index++) {
            return(
                <FontAwesome name="heart" size={24} color="lightred" />
            )
        }
    }
    return(
        <View style={styles.reviewParent}>
        {/* <CardComponent> */}
            <View style={styles.cardStyle}>
                <View style={styles.gameContent}>
                    <Text style={styles.gameTitle}>{gameItem.title}</Text>
                    <Text style={styles.gameDesc}>{gameItem.desc}</Text>
                </View>
                <Image source={gameItem.img} style={styles.gameImage} fadeDuration={0} />
            </View>
            <View style={styles.rating}>        
               <Rating/>
            </View>
            {/* </CardComponent> */}
            <StatusBar style="dark" />
        </View>
    );

};

const styles = StyleSheet.create({
    reviewParent: {
        paddingVertical:20,
        paddingHorizontal: 15,
        flex: 1,
    },
    cardStyle: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        // flexWrap: 'wrap',
        // marginVertical: 20
    },
    gameTitle: {
        fontFamily: 'RobotoCondensed_400Regular',
        fontSize: 22,
        alignSelf: 'center',
    },
    gameImage: { 
        width: 50,
        height: 50,
        alignSelf: 'center',
    },
    gameContent:{
        flexDirection: 'column',
    },
    gameDesc:{
        marginHorizontal: 10,
        alignSelf: 'center',
    }
});
