import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { useFonts } from 'expo-font';
import { globalStyles } from "./styles/globalStyles";
import { HomeScreen } from './components/HomeScreen';

export default function App() {
  const [loaded, err] = useFonts({
    RobotoCondensedRegular: require('./fonts/RobotoCondensed-Regular.ttf'),
    RobotoCondensedBold: require('./fonts/RobotoCondensed-Bold.ttf'),
    // RobotoCondensedBoldItalic: require('./fonts/RobotoCondensed-BoldItalic.ttf'),
    // RobotoCondensedItalic: require('./fonts/RobotoCondensed-Italic.ttf'),

  });
  const [games, modifyGame] = useState([
    { title: 'Apex Legends', rating: 5, desc: 'A very nice game to play!'},
    { title: 'Fortnite', rating: 4, desc: 'A very nice game to play!'},
    { title: 'GTA V', rating: 3, desc: 'A very nice game to play!'},
  ]);
  const addGame = (val)=>{
    val.key = Math.random().toString();
    modifyGame([
      val, 
      ...games
    ])
  };
  if (!loaded) {
    console.log(err);
    return null;
  }
  return (
    <View style={styles.container}>
      <Text style={globalStyles.headerTitleText}>Open up App.js to start working on your app!</Text>
      {/* <HomeScreen games={games} navigation={''}/> */}
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textStyle:{
    fontFamily: 'RobotoCondensedRegular',
    fontSize: 18
  }
});
