import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import  AppLoading  from 'expo-app-loading';
import {
  useFonts,
  RobotoCondensed_300Light,
  RobotoCondensed_300Light_Italic,
  RobotoCondensed_400Regular,
  RobotoCondensed_400Regular_Italic,
  RobotoCondensed_700Bold,
  RobotoCondensed_700Bold_Italic
} from '@expo-google-fonts/roboto-condensed';
// import { globalStyles } from "./styles/globalStyles";
import  HomeScreen  from './components/HomeScreen';
export default function App() {
  const [loaded] = useFonts({
    RobotoCondensed_400Regular,
  });
  const [games, modifyGame] = useState([
    { title: 'Apex Legends', rating: 5, desc: 'A very nice game to play!', key: '1'},
    { title: 'Fortnite', rating: 4, desc: 'A very nice game to play!', key: '2'},
    { title: 'GTA V', rating: 3, desc: 'A very nice game to play!', key: '3'},
  ]);
  const addGame = (val)=>{
    val.key = Math.random().toString();
    modifyGame([
      val, 
      ...games
    ])
  };
  if (!loaded) {
    return (
      <AppLoading />
    );
  }
  return (
    <View style={styles.container}>
      {/* <Text style={globalStyles.headerTitleText}>Open up App.js to start working on your app!</Text> */}
      <HomeScreen games={games} navigation={''}/>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff'
  },
  textStyle:{
    fontFamily: 'RobotoCondensed_400Regular',
    fontSize: 18
  }
});
