import {StyleSheet} from 'react-native';

export const globalStyles = StyleSheet.create({
    headerTitleText : {
        fontFamily: 'RobotoCondensedBold',
        fontSize : 28,
    },
    headerView : {
        alignItems : 'center',
    },
    cardBasicStyle:{
        borderRadius: 15,
        flex: 1,
        shadowOffset: 3,
        shadowColor: '#333',
    }
});
